mapper.select:
select id, order_no AS orderNo, status, pay_type AS payType, amount, type, create_time AS createTime, operator_id AS operatorId, operator_name AS operatorName from order_main where is_delete = 0
and order_no like CONCAT('%',#{orderNo},'%')
and status = #{status}
and operator_name like CONCAT('%',#{operatorName},'%')
ORDER BY create_time DESC LIMIT #{offset}, #{pageSize}
